def clean_string(s):
    s = s.lower().strip()
    s = re.sub(r"([.,!?])", r" \1", s)
    s = re.sub(r"[^a-zA-Z.!?]+", r" ", s)
    
    for dic in [contraction_mapping, mispell_dict, char_mapping]:
        for word in dic.keys():
            s = s.replace(word, dic[word])
    return s

def tokenize(s):
    return [str(s) for s in nlp(s)]

def get_labels_ix_dict(labels):
    return dict(zip(labels, range(len(labels))))

def get_vocabulary(train_df):
    tokens = []
    counter = Counter()
    for sentence_pair in zip(train_df["clean1"],train_df["clean2"]):
        tokens1, tokens2 = tokenize(sentence_pair[0]), tokenize(sentence_pair[1])
        counter.update(tokens1)
        counter.update(tokens2)
        tokens.append(tokens1 + tokens2)
    vocabulary = list(set([word for word in counter]))
    vocabulary = ["<PAD>", "<UNK>"] + vocabulary
    return vocabulary, tokens

def get_weights_matrix(embeddings, word_to_ix, dims, oov_init_zero=False):
    
    M = np.zeros(dims)
    vocabulary_dim, word_embedding_dim = dims
    
    for word, i in word_to_ix.items():
        try:
            M[i] = embeddings[word]
        except KeyError:
            if oov_init_zero:
                embeddings[word] = np.random.normal(scale=0.6, size=(1,word_embedding_dim))
            continue
    return M